  const yRange = useTransform(scrollYProgress, (v) => {
    let res = 0;
    if (v <= 0.2) {
      res = 0;
    } else if ((v > 0.2) & (v <= 0.35)) {
      res = transform(v, [0.2, 0.35], [0, 0.2]);
    } else if ((v > 0.35) & (v <= 0.8)) {
      res = transform(v, [0.35, 0.8], [0.2, 0.55]);
    } else if ((v > 0.8) & (v <= 0.9)) {
      res = transform(v, [0.8, 0.9], [0.55, 0.65]);
    } else {
      res = transform(v, [0.9, 1], [0.65, 1]);
    }
    //console.log({ v, res });
    return res;
  });
